import React, { useState, Fragment } from "react";
import { useDispatch } from "react-redux";
import {
  removeItemShopping,
  registryCourse
} from "../../../../redux/actions/KhoaHocAction";
import LoadingPage from "../../LoadingPage/LoadingPage";
export default function Checkout(props) {
  const [listItem, setListItem] = useState(props.listItem);
  const [loading, setLoading] = useState(false);
  const dispath = useDispatch();
  console.log(listItem[0]);
  const RegistryCourse = () => {
    let regis = async item => {
      let data = {
        maKhoaHoc: item.maKhoaHoc,
        taiKhoan: "demo"
      };
      setLoading(true);
      let result = await registryCourse(data);
      console.log(result);
      if (result !== "Đã đăng ký khóa học này rồi!") {
        dispath(removeItemShopping(item));
      }
      alert(`Khóa học:${item.maKhoaHoc}. ` + result);
      setLoading(false);
    };

    listItem.map(item => {
      regis(item);
    });
  };
  return (
    <Fragment>
      {loading ? (
        <LoadingPage></LoadingPage>
      ) : (
        <div className="container-fluid w-100 h-100 px-0 checkout my-5">
          <div className="container">
            <div className="row">
              <div className="col-12 col-lg-4 px-5">
                <h3>Your Item</h3>
                {listItem.map((item, index) => {
                  return (
                    <div className="row my-3 py-3 item" key={index}>
                      <div className="col-lg-3 px-0 text-center">
                        <img
                          className="w-75 "
                          src={item.hinhAnh}
                          alt="hinhanh"
                        />
                      </div>
                      <div className="col-9 col-lg-9">
                        <p className="m-0">
                          <b>{item.tenKhoaHoc}</b>
                        </p>
                        <p className="m-0">
                          By Martin Perhiniak, Yes I'm a Designer
                        </p>
                        <div>
                          <span className="promotion px-2">$19.3</span>
                          <span className="price">$25.5</span>
                        </div>
                      </div>
                    </div>
                  );
                })}

                {/*  */}
              </div>
              <div className="col-12 col-lg-8 checkout__if">
                <div className="price__total">
                  <h1>
                    Total: <span>${props.totalPrice}</span>
                  </h1>
                  <span>$59.66</span>
                </div>
                <div className="payment row my-3">
                  <div className="col-4 col-lg-3 text-center">
                    <img
                      className="w-50"
                      src="data:image/jpeg;base64,"
                      alt=""
                    />
                  </div>
                  <div className="col-4 col-lg-3 text-center">
                    <img
                      className="w-50"
                      src="data:image/png;base64,"
                      alt=""
                    />
                  </div>
                  <div className="col-4 col-lg-3 text-center">
                    <img
                      className="w-50"
                      src="data:image/jpeg;base64,"
                      alt=""
                    />
                  </div>
                </div>
                <div className="payment__form my-5">
                  <form action="">
                    <h5>Confirm information</h5>
                    <div class="input-group mb-3">
                      <div className="input-group-prepend">
                        <span className="input-group-text px-3">
                          <i class="fas fa-user    "></i>
                        </span>
                      </div>
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Name of card"
                      />
                    </div>
                    <div class="input-group mb-3">
                      <div className="input-group-prepend">
                        <span className="input-group-text card__num">
                          <i class="fas fa-code    "></i>
                        </span>
                      </div>
                      <input
                        type="text"
                        className="form-control "
                        placeholder="Card number"
                      />
                    </div>
                    <div className="row my-4">
                      <div className="col-lg-1 text-right px-0 card__icon">
                        <i class="fas fa-credit-card    "></i>
                      </div>
                      <div className="col-lg-5 px-0 ml-1">
                        <input
                          className="w-100 card__date"
                          type="text"
                          placeholder="MM/YY"
                        />
                      </div>
                      <div className="col-lg-5 px-0">
                        <input
                          className="w-100 card__code "
                          type="text"
                          placeholder="Security Code"
                        />
                      </div>
                    </div>
                    <button
                      className="btn btn-warning"
                      type="button"
                      onClick={() => RegistryCourse()}
                    >
                      Complete Payment
                    </button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </Fragment>
  );
}
